mapper.select:
        SELECT
        
        

        FROM announcement  a

        
            
                and a.id in
                
                #{item}
                
            
            
                and a.announcement_name like CONCAT('%',#{params.announcementName},'%')
            
            
                and a.announcement_types = #{params.announcementTypes}
            
            
                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 
            
            
                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 
            
            
                and a.announcement_content like CONCAT('%',#{params.announcementContent},'%')
            

        
        order by a.${params.sort} ${params.order}
